import {core,HTMLElement} from "dhtml-weixin"
export const GUI = class {
  constructor() {
    const page = core.Page.current
      this._gui = page.selectComponent("#gui")
      this.domElement = new HTMLElement()
  }
  destroy(){
    this._gui.destroy ()
  }
  title(title){
    return this._gui.title(title) 
  }
  onChange(){
    return this._gui.onChange.apply(this._gui, arguments)
  }
  addFolder() {
    return this._gui.addFolder.apply(this._gui, arguments)
  }
  add() {
    return this._gui.add.apply(this._gui, arguments)
  }
  addColor(){
    return this._gui.addColor.apply(this._gui, arguments)  
  }
  addBoolean(){
    return this.GUI.addBoolean.apply(this._gui,arguments)  
  }
  addNumber(){
    return this.GUI.addNumber.apply(this._gui,arguments)  
  }
  addString(){
    return this.GUI.addString.apply(this._gui,arguments)  
  }
  addOptions(){
    return this.GUI.addOptions.apply(this._gui,arguments)  
  }
  addFunction(){
    return this.GUI.addFunction.apply(this.GUI,arguments)  
  }
  open(){
    return this._gui.open()
  }
  close(){
    return this._gui.close()
  }
}